Property.Value:
SELECT fn_adminsecuritypredicate_result FROM ( SELECT 1 AS fn_adminsecuritypredicate_result FROM base.TenantUser tu INNER JOIN base.[User] u ON u.Userid = tu.UserId WHERE
CAST(SESSION_CONTEXT(N'UserId') AS UniqueIdentifier) = tu.UserId AND tu.TenantId = @tenantId AND u.Isadmin = 1
) AS U WHERE U.fn_adminsecuritypredicate_result = 1

SELECT fn_usersecuritypredicate_result FROM ( SELECT 1 AS fn_usersecuritypredicate_result FROM base.TenantUser tu WHERE
CAST(SESSION_CONTEXT(N'UserId') AS UniqueIdentifier) = tu.UserId AND tu.TenantId = @tenantId
) AS U WHERE U.fn_usersecuritypredicate_result = 1
